
public class VariableDemo {

	public static void main(String[] args) {
		//* TODO Auto-generated method stub
 byte byteNumber;
 byteNumber = 10;
 System.out.println(byteNumber);

	short shortNumber;
	shortNumber = 1234;
	System.out.println(shortNumber);
	
	int intNumber;
	intNumber = 454545;
	System.out.println(intNumber);
	long longNumber;
	longNumber = 123148215654561l;
	System.out.println(longNumber);
	
	//Decalring variables with floating point data types
	float floatNumber;
	floatNumber = 3.5552546416f;
	System.out.println(floatNumber);
			
	double doubleNumber;
	doubleNumber = 284634894;
	System.out.println(doubleNumber);
	
	//Decalring variable with boolean data type
	boolean trueOrFalse;
	trueOrFalse = true;
	System.out.println(trueOrFalse);
	
	
	//dealring variable with char data type
	char character;
	character = 'a';
	System.out.println(character);
	
 
 
 
	}

}
